#!/bin/bash
echo +++++++++++++++++++++++++NETSTAT -You can change the arguments to what fits your interest+++++++++++++++++++++++++ >> perfstats.txt ; netstat -tlp >> perfstats.txt; echo +++++++++++++++++++++++++UPTIME-Note: load average should be below the number of CPUs+++++++++++++++++++++++++>> perfstats.txt; uptime >> perfstats.txt ; echo +++++++++++++++++++++++++IOSTAT :You can change the arguments to what fits your interest+++++++++++++++++++++++++>> perfstats.txt ; iostat >> perfstats.txt; echo +++++++++++++++++++++++++MPSTAT -You can change the arguments to what fits your interest+++++++++++++++++++++++++>> perfstats.txt; mpstat >> perfstats.txt ; echo +++++++++++++++++++++++++MEMORY-free : You can change the arguments to what fits your interest+++++++++++++++++++++++++ >> perfstats.txt ; free -m >> perfstats.txt ; echo +++++++++++++++++++++++++VMSTAT :You can change the arguments to what fits your interest+++++++++++++++++++++++++>> perfstats.txt; vmstat >> perfstats.txt;

